import React from 'react';
import './ProjectGrid.css';  // Archivo CSS

const ProjectGrid = () => {
  const projects = [
    { type: 'image', src: '/assets/images/project1.jpg', link: 'https://example.com/project1' },
    { type: 'video', src: '/assets/videos/project2.mp4', link: 'https://example.com/project2' },
    { type: 'image', src: '/assets/images/project3.jpg', link: 'https://example.com/project3' },
    { type: 'video', src: '/assets/videos/project4.mp4', link: 'https://example.com/project4' },
    { type: 'image', src: '/assets/images/project5.jpg', link: 'https://example.com/project5' },
    { type: 'video', src: '/assets/videos/project6.mp4', link: 'https://example.com/project6' },
    // Agrega más proyectos aquí
  ];

  return (
    <div className="grid-container">
      {projects.map((project, index) => (
        <a
          key={index}
          href={project.link}
          target="_blank"
          rel="noopener noreferrer"
          className="grid-item"
        >
          {project.type === 'image' ? (
            <img src={project.src} alt={`Project ${index + 1}`} />
          ) : (
            <video src={project.src} autoPlay muted loop />
          )}
        </a>
      ))}
    </div>
  );
};

export default ProjectGrid;
