import React from 'react';
import './ProjectGrid.css';

const ProjectGrid = () => {
  const projects = [
    { type: 'image', src: '/assets/images/project1.jpg', title: 'Project 1' },
    { type: 'video', src: '/assets/videos/project2.mp4', title: 'Project 2' },
    { type: 'image', src: '/assets/images/project3.jpg', title: 'Project 3' },
    { type: 'video', src: '/assets/videos/project4.mp4', title: 'Project 4' },
  ];

  return (
    <div className="grid-container">
      {projects.map((project, index) => (
        <div key={index} className="grid-item">
          {project.type === 'image' ? (
            <img src={project.src} alt={`Project ${index + 1}`} />
          ) : (
            <video src={project.src} autoPlay muted loop />
          )}
          <div className="project-title glitch" data-text={project.title}>
            {project.title}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProjectGrid;
