import React, { useState } from 'react';
import './ProjectGrid.css';  // Archivo CSS

const ProjectGrid = () => {
  // Definimos las etiquetas de cada proyecto junto con el título
  const projects = [
    { type: 'image', src: '/assets/images/project1.jpg', link: 'https://example.com/project1', tag: 'design', title: 'Project 1' },
    { type: 'video', src: '/assets/videos/project2.mp4', link: 'https://example.com/project2', tag: 'ux/ui', title: 'Project 2' },
    { type: 'image', src: '/assets/images/project3.jpg', link: 'https://example.com/project3', tag: 'dev', title: 'Project 3' },
    { type: 'video', src: '/assets/videos/project4.mp4', link: 'https://example.com/project4', tag: 'design', title: 'Project 4' },
    { type: 'image', src: '/assets/images/project5.jpg', link: 'https://example.com/project5', tag: 'dev', title: 'Project 5' },
    { type: 'video', src: '/assets/videos/project6.mp4', link: 'https://example.com/project6', tag: 'ux/ui', title: 'Project 6' },
  ];

  // Estado para controlar el filtro seleccionado
  const [selectedTag, setSelectedTag] = useState('all');

  // Función que maneja el filtro basado en la etiqueta seleccionada
  const filteredProjects = projects.filter(project =>
    selectedTag === 'all' ? true : project.tag === selectedTag
  );

  return (
    <div>
      <div className="filter-buttons">
        <button onClick={() => setSelectedTag('all')}>All</button>
        <button onClick={() => setSelectedTag('design')}>Design</button>
        <button onClick={() => setSelectedTag('ux/ui')}>UX / UI</button>
        <button onClick={() => setSelectedTag('dev')}>Dev</button>
      </div>

      <div className="grid-container">
        {filteredProjects.map((project, index) => (
          <a
            key={index}
            href={project.link}
            target="_blank"
            rel="noopener noreferrer"
            className="grid-item"
          >
            {project.type === 'image' ? (
              <img src={project.src} alt={`Project ${index + 1}`} />
            ) : (
              <video src={project.src} autoPlay muted loop />
            )}
            <div className="project-title glitch">{project.title}</div>
          </a>
        ))}
      </div>
    </div>
  );
};

export default ProjectGrid;
